mapper.select:
select 

,

from  message


        and fromUser = #{fromuser,jdbcType=VARCHAR}


        and toSgdId = #{tosgdid,jdbcType=VARCHAR}


        and type = #{type,jdbcType=INTEGER}


        and DATE_FORMAT(time,'%Y/%m/%d') = #{time}


        and attachmentId = #{attachmentid,jdbcType=VARCHAR}


       and content = #{content,jdbcType=LONGVARCHAR}

select 

,
,
user.EXT1 userFullName, sgdxx.sgdmc, DATE_FORMAT(time,'%Y-%m-%d %H:%i:%s') as formattedTime
from  message left join p_user user on message.fromUser = user.USER_ID
left join sgdxx on message.toSgdId = sgdxx.id


        and fromUser = #{fromuser,jdbcType=VARCHAR}


        and toSgdId = #{tosgdid,jdbcType=VARCHAR}


        and type = #{type,jdbcType=INTEGER}


        and DATE_FORMAT(time,'%Y-%m-%d') = #{time}


        and attachmentId = #{attachmentid,jdbcType=VARCHAR}


        content = #{content,jdbcType=LONGVARCHAR}


order by time
