select a.USER_ID, a.USER_NAME, a.USER_PWD, a.IS_GUEST, a.IS_LOCKED, a.ROLE_ID, a.ORG_ID, a.CREATE_DATE, a.UPDATE_DATE, a.CREATE_USER, a.openid, a.wechatNo, a.name, a.REMARK, a.gender, a.birthday, a.id_no, a.education, a.phone, a.email, a.major, a.gangwei, a.yongglx, a.gongzsj, a.gongzds, a.bendsgzkssj, a.zhengsmc, a.ziz_no, a.ziz_time, a.ziz_org, a.ziz_endtime, a.baox, a.other, b.sgdmc, d.ROLE_NAME from p_user a left join sgdxx b on a.ORG_ID = b.id left join role d on a.ROLE_ID = d.ROLE_ID where a.USER_NAME != 'admin'
AND ( a.USER_NAME LIKE concat('%',#{searchKey},'%') OR d.ROLE_NAME LIKE concat('%',#{searchKey},'%') OR a.name LIKE concat('%',#{searchKey},'%') )
limit #{limit1}, #{limit2}

